ALTER TABLE projet ADD CONSTRAINT projet_prospect_id_prospect_id FOREIGN KEY (prospect_id) REFERENCES prospect(id);
ALTER TABLE projet_event ADD CONSTRAINT projet_event_type_id_projet_event_type_id FOREIGN KEY (type_id) REFERENCES projet_event_type(id);
